// App.js
import React from 'react';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      saveEmail: '',
    };
  }

  changeEmail(value) {
    this.setState({ email: value });
  }

  changeSaveEmail(value) {
    this.setState({ saveEmail: value, email: '' });
  }

  render() {
    return (
      <div className="App" >
        <label htmlFor="id-email">
          Email
          </label>
        <input id="id-email" type="email" value={ value } onChange={ (e) => this.changeEmail(e.target.value) } />
        <input id="btn-send" type="button" value="Enviar"
          data-testid="id-send" />
        <input id="btn-back" type="button" value="Voltar" />
      </div>
    );
  }
}

export default App;
